mapper.update:
update user
set username = #{username,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  status = #{status,jdbcType=INTEGER},
  descn = #{descn,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, username, password, status, descn
from user
where id = #{id,jdbcType=BIGINT}
